mapper.select:
SELECT 

FROM gen_table a

WHERE a.id = #{id}

SELECT 

FROM gen_table a

WHERE a.del_flag = #{DEL_FLAG_NORMAL} 

	AND a.name = #{name}


	AND a.name LIKE concat('%',#{nameLike},'%')


	AND a.comments LIKE
		concat('%',#{comments},'%')


	AND a.parent_table = #{parentTable}


	AND a.project_id =#{projectId}



		ORDER BY ${page.orderBy}


		ORDER BY a.name ASC

SELECT 

FROM gen_table a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.project_id =#{projectId}



		ORDER BY ${page.orderBy}


		ORDER BY a.name ASC

mapper.update:
UPDATE gen_table SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
