mapper.select:
SELECT * FROM MODULE WHERE id IN

     #{item}

 ORDER BY createTime ASC

mapper.update:
UPDATE MODULE


        id = #{id},


        name = #{name},


        host = #{host},


        description = #{description},


        lastUpdateTime = #{lastUpdateTime},


        projectId = #{projectId},


        createTime = #{createTime}


        requestHeaders = #{requestHeaders}


        requestArgs = #{requestArgs}



WHERE id = #{id}
