template.content:
public class ${ClassName}Controller{
	@Autowired
	private ${ClassName}Service ${className}Service;

	  <#list interfaces as m>
		<#if m.description??>
     /**
	 *  ${m.description}
	 * @param ${className}Dto
	 * @return Object
	 */
	 </#if>
	 <#if m.name?? && m.inputParam??>
	@RequestMapping(value = "/${m.name}")
    public Object ${m.name}(${m.inputParam}) {

		try {
		<#if secoendType.putParam??>
			Object o = ${className}Service.${m.name}(${m.putParam});
		<#/if>
			if(o!=null)
            {
            	return JsonResponse.success(o);
            }
            else
            {
            	return JsonResponse.fail("${ClassName}的${apiName}()方法fail");
            }
        }catch(Exception e){
            return JsonResponse.error("${ClassName}的${m.name}()方法error", e);
        }
    }
	</#if>
    </#list>
}
}
